FOREIGN KEY(id_word) REFERENCES raw_versese(id));
CREATE TABLE id_json_words (
        id_json INT,
        words VARCHAR(65535), 
        FOREIGN KEY (id_json) REFERENCES raw_verses(id) 
        );
COMMIT;
